import React from 'react';
import { useState, useEffect } from 'react';
import Navbar from '../src/components/navbar';
import Tasks from './components/Tasks';
import Modal from './components/Modal';
import './components/style.css';
import 'bootstrap/dist/css/bootstrap.min.css';





function App() {
  const [showModal, setShowModal]=useState(false)
  const [tasks, setTasks] = useState([])
 useEffect(() =>{
  const getEvents = async() => {
    const eventsFronServer = await fetchEvents() 
    setTasks(eventsFronServer)
  }
  getEvents()
   
 },[]
 )
//  fetch events 
 const fetchEvents = async () => {
  const res = await fetch('http://localhost:5000/events')
   const data = await res.json()
   console.log(data)

   return data
 }
    // Delete task 
    const deleteTask = (id) => {
      console.log('deleted', id)
      setTasks(tasks.filter((tasks) => tasks.id !==id))
    }
// Add event
    const addEvent =(task)=>{
      const id= Math.floor(Math.random()  *10000) + 1
      console.log('Added', id)
      const newTask = {id, ...task}
      setTasks([...tasks, newTask])
    }
// Toggle Reminder
    const toggleReminder = (id) =>{
    console.log(id,'DoubleClicked')
    setTasks(tasks.map((tasks) => tasks.id === id ? {...tasks, reminder:
       !tasks.reminder} : tasks
        )
      )
    }
  return (
    <div className='Task-Body mt-5 p-3'>
      <Navbar 
      title='Task Tracker'
      onAdd={() => setShowModal(!showModal)
      }
      showModal={showModal}
      />
      {showModal && <Modal
      onAdd={addEvent}
      />}
      {tasks.length > 0 ? (
      <Tasks tasks={tasks}
      onDelete={deleteTask}
      onToggle={toggleReminder}
      />
      ) : ('No Event Available')}
    </div>
  );
}

export default App;
